import{_ as i,c as a,o,ao as l}from"./chunks/framework.CIBfAoRC.js";const f=JSON.parse('{"title":"系统要求","description":"","frontmatter":{},"headers":[],"relativePath":"guide/system-requirements.md","filePath":"guide/system-requirements.md","lastUpdated":1768549686000}'),r={name:"guide/system-requirements.md"};function t(s,e,c,n,d,h){return o(),a("div",null,[...e[0]||(e[0]=[l('<h1 id="系统要求" tabindex="-1">系统要求 <a class="header-anchor" href="#系统要求" aria-label="Permalink to “系统要求”">​</a></h1><p>在使用 NS_word 文档处理工具之前，请确保您的系统满足以下要求。</p><h2 id="操作系统" tabindex="-1">操作系统 <a class="header-anchor" href="#操作系统" aria-label="Permalink to “操作系统”">​</a></h2><ul><li><strong>Windows 10</strong> 或更高版本</li><li>支持 64 位系统架构</li></ul><h2 id="文件格式" tabindex="-1">文件格式 <a class="header-anchor" href="#文件格式" aria-label="Permalink to “文件格式”">​</a></h2><ul><li>支持 <strong><code>.docx</code></strong> 格式的 Word 文档</li><li>不支持 <code>.doc</code> 格式（旧版 Word 格式）</li></ul><div class="warning custom-block"><p class="custom-block-title">重要提示</p><p>请确保您的文档是 <code>.docx</code> 格式。如果是 <code>.doc</code> 格式，请先在 Microsoft Word 中打开并另存为 <code>.docx</code> 格式。</p></div><h2 id="microsoft-office" tabindex="-1">Microsoft Office <a class="header-anchor" href="#microsoft-office" aria-label="Permalink to “Microsoft Office”">​</a></h2><p>虽然本工具可以独立运行，但为了确保最佳兼容性和文档格式的完整性，建议：</p><ul><li>安装 <strong>Microsoft Office</strong>（Word 2016 或更高版本）</li><li>或者使用 <strong>Microsoft 365</strong></li></ul><div class="tip custom-block"><p class="custom-block-title">为什么需要 Office？</p><p>生成的模板文档和拆分后的文档建议使用 Microsoft Office 打开一次并重新保存，这样做的目的是：</p><ul><li>使用 Office 的自动修复功能</li><li>确保文档格式的完整性和兼容性</li><li>修复可能的格式问题</li></ul></div><h2 id="硬件要求" tabindex="-1">硬件要求 <a class="header-anchor" href="#硬件要求" aria-label="Permalink to “硬件要求”">​</a></h2><ul><li><strong>内存</strong>：建议 4GB 或以上</li><li><strong>存储空间</strong>：至少 100MB 可用空间</li><li><strong>处理器</strong>：现代多核处理器（推荐）</li></ul><h2 id="文档要求" tabindex="-1">文档要求 <a class="header-anchor" href="#文档要求" aria-label="Permalink to “文档要求”">​</a></h2><h3 id="拆分文档功能" tabindex="-1">拆分文档功能 <a class="header-anchor" href="#拆分文档功能" aria-label="Permalink to “拆分文档功能”">​</a></h3><ul><li>文档必须包含<strong>一级标题</strong>（标题 1 样式）</li><li>系统会自动识别所有一级标题作为章节</li><li>系统会自动排除目录类标题（目录、表目录、图目录等）</li></ul><h3 id="合并文档功能" tabindex="-1">合并文档功能 <a class="header-anchor" href="#合并文档功能" aria-label="Permalink to “合并文档功能”">​</a></h3><ul><li>确保文档中的题注使用正确的标签格式 <ul><li><strong>中文模式</strong>：使用&quot;表&quot;和&quot;图&quot;作为题注标签</li><li><strong>英文模式</strong>：使用&quot;Table&quot;和&quot;Figure&quot;作为题注标签</li></ul></li></ul>',18)])])}const m=i(r,[["render",t]]);export{f as __pageData,m as default};
